/*........................................*/
double PolarR6SonnendruckerTriangular::x(double r, double theta, double map2_epsilon, double map2_e, double Rmax) const
{
    return (1.0 - sqrt(map2_epsilon * (map2_epsilon + 2.0 * (r/Rmax) * cos(theta)) + 1.0)) / map2_epsilon;
}
/*........................................*/
void PolarR6SonnendruckerTriangular::x(std::vector<double> const& r, double theta, double map2_epsilon, double map2_e, double Rmax, std::vector<double>& sol) const
{
    for (std::size_t i=0; i < sol.size(); ++i)
    {
        sol[i] = (1.0 - sqrt(map2_epsilon * (map2_epsilon + 2.0 * (r[i]/Rmax) * cos(theta)) + 1.0)) / map2_epsilon;
    }
}
/*........................................*/
